import React, { Component } from 'react';
import './index.css';
import { connect } from 'dva';
import { routerRedux } from 'dva/router';

import Slider from './slider';

import { play, stop, musicMenu, back, add, love, love_red, error } from '../../assets/asset'

@connect(({ index }) => ({
    index
}))
class recommend extends Component {
	state = {
		song: [],
		dang: [],
		loveList: [],
		menuList: []
	}

	componentDidMount() {
		this.getSong()
		this.getDang()
		this.getlove()
		this.getMenu()
	}

	componentWillReceiveProps() {
		this.getlove()
		this.getMenu()
	}

	getlove = () => {
		let b = JSON.parse(localStorage.getItem('love')) || [];
		let c = []
		b.forEach((item)=>{
			c.push(item.id)
		})
		this.setState({
			loveList: c,
		})
	}

	getSong = () => {
		const { dispatch } = this.props;
		dispatch({
			type: 'index/getSong'
		})
		.then((res)=>{
			// console.log(res)
			this.setState({
				song: res.result
			})
		})
	}

	getDang = () => {
		const { dispatch } = this.props;
		dispatch({
			type: 'index/getDang'
		})
		.then((res)=>{
			// console.log(res)
			this.setState({
				dang: res.result
			})
		})
	}

	pushSong = () => {
		const { dispatch } = this.props;
		sessionStorage.setItem('songPage', JSON.stringify({ type: 'index/getRankSong', payload: 1 }))
		let a = JSON.parse(sessionStorage.getItem('title')) || []
		a.push({ name: '热门歌曲', type: '热门歌曲' })
		sessionStorage.setItem('title', JSON.stringify(a))
		dispatch(routerRedux.push('/song'));
	}

	pushSongList = () => {
		const { dispatch } = this.props;
		sessionStorage.setItem('songListPage', JSON.stringify({ type: 'index/getSongList' }))
		let a = JSON.parse(sessionStorage.getItem('title')) || []
		a.push({ name: '热门歌单', type: '热门歌单' })
		sessionStorage.setItem('title', JSON.stringify(a))
		dispatch(routerRedux.push('/songList'));
	}

	pushSinger = () => {
		const { dispatch } = this.props;
		sessionStorage.setItem('singerPage', JSON.stringify({ type: 'index/getHotSinger' }))
		let a = JSON.parse(sessionStorage.getItem('title')) || []
		a.push({ name: '热门歌手', type: '热门歌手' })
		sessionStorage.setItem('title', JSON.stringify(a))
		dispatch(routerRedux.push('/singer'));
	}

	pushSong2 = (item) => {
		const { dispatch } = this.props;
		sessionStorage.setItem('songPage', JSON.stringify({ type: 'index/getSongListDetail', payload: item.id, item }))
		let a = JSON.parse(sessionStorage.getItem('title')) || []
		a.push({ name: item.name, type: 'list' })
		sessionStorage.setItem('title', JSON.stringify(a))
		dispatch(routerRedux.push('/song'));
	}

	// pushSingerDetail = (item) => {
	// 	sessionStorage.setItem('singerDetail',JSON.stringify({name: item.name, id: item.id, pic: item.img1v1Url}))
	// 	const { dispatch } = this.props;
	// 	dispatch(routerRedux.push('/singerDetail'))
	// }

	

	// getBase64 = (imgUrl) => {
	// 	window.URL = window.URL || window.webkitURL;
	// 	var xhr = new XMLHttpRequest();
	// 	xhr.open('get', imgUrl, true);
	// 	// 至关重要
	// 	xhr.responseType = 'blob';
	// 	xhr.onload = function () {
	// 		if (this.status == 200) {
	// 			//得到一个blob对象
	// 			var blob = this.response;
	// 			console.log('blob', blob)
	// 			//  至关重要
	// 			let oFileReader = new FileReader();
	// 			oFileReader.onloadend = function (e) {
	// 				let base64 = e.target.result;
	// 				console.log('方式一》》》》》》》》》', base64)
	// 			};
	// 			oFileReader.readAsDataURL(blob);

	// 		}
	// 	}
	// 	xhr.send();
	// }

	changeMusic = (item) => {
		const { dispatch } = this.props;
		let ar = '';
		let picUrl;
		if(item.ar) {
			item.ar.forEach((item, index) => {
				ar += item.name + ' '
			})
			picUrl= item.al.picUrl
		} else {
			item.song.artists.forEach((item, index) => {
				ar += item.name + ' '
			})
			picUrl= item.song.album.blurPicUrl
		}
		
		let payload = {
			id: item.id,
			name: item.name,
			picUrl: picUrl,
			ar: ar,
			src: `https://music.163.com/song/media/outer/url?id=${item.id}.mp3`
		}

		let a = JSON.parse(localStorage.getItem('history')) || []
		let b = a.filter((item2)=>{
            return item.id != item2.id
		})
		b.unshift(item)
		if(b.length >= 11) {
			b.shift()
		}
		console.log(b)
		localStorage.setItem('history',JSON.stringify(b))
		

		dispatch({
			type: 'index/setMusic',
			payload
		})
	}


	addMusic = (item) => {
		const { dispatch } = this.props
		let a = JSON.parse(localStorage.getItem('musicMenu')) || []
		let b = true
		a.forEach((item2)=>{
			if(item2.id == item.id) {
				b = false;
			}
		})

		if(b) {
			a.push(item)
			localStorage.setItem('musicMenu', JSON.stringify(a))
			dispatch({
				type: 'index/update'
			})
		}
		
	}

	musicPlay = (isPause) => {
		const { dispatch } = this.props;
		dispatch({
			type: 'index/setPause',
			isPause
		})
	}

	loveMusic = (item, love) => {
		const { dispatch } = this.props;

		let b = JSON.parse(localStorage.getItem('love')) || [];
		let c = b.filter((item2)=>{
			return item.id != item2.id
		})
		if(love) {
			c.push(item);
		}
		localStorage.setItem('love', JSON.stringify(c))
		dispatch({
			type: 'index/update'
		})
	}

	getMenu = () => {
		let b = JSON.parse(localStorage.getItem('musicMenu')) || []
		let c = [];
		b.forEach((item)=>{
			c.push(item.id)
		})
		this.setState({
			menuList: c,
		})
	}
    
    delMusic = (item) => {
		const { dispatch } = this.props;
        const par = JSON.parse(localStorage.getItem('musicMenu')) || []
        let a = par.filter((item2)=>{
            return item.id != item2.id
        })
		localStorage.setItem('musicMenu', JSON.stringify(a))
		dispatch({
			type: 'index/update'
		})
	}

	render() {
		const { song, dang, loveList, menuList } = this.state;
		const { music: { id, isPause } } = this.props.index

		return (
			<div>
				<Slider />

				<div className="pop">
					<div className="pop_box song" onClick={this.pushSong}>
						<img className="pop_img" src="data:image/png;base64," />
						<div className="pop_text">热门歌曲</div>
					</div>
					<div className="pop_box dang" onClick={this.pushSongList}>
						<img className="pop_img" src="data:image/png;base64," />
						<div className="pop_text">热门歌单</div>
					</div>
					<div className="pop_box sing" onClick={this.pushSinger}>
						<img className="pop_img" src="data:image/png;base64," />
						<div className="pop_text">热门歌手</div>
					</div>
				</div>

				<div className="recommend">
					<div className="recommend_title">最新音乐</div>
					{/* {
						song.map((item, index)=>{
							if(index < 9) {
								return <div className="recommend_box" key={index} onClick={() => this.changeMusic(item)}>
									<img className="recommend_img" src={item.song.album.blurPicUrl} />
									<div className="recommend_text">{item.name}</div>
								</div>
							}
						})
					} */}
					{
						song.map((item, index)=>{
							return <div className="songList" key={index}>
								<div className="songName">{item.name}</div>
								<div className="songArtists">{item.song.artists && item.song.artists.map((item, index) => { return ` ${item.name} ` })}</div>
								<img src={loveList.indexOf(item.id) == -1 ? love : love_red} onClick={loveList.indexOf(item.id) == -1 ? () => this.loveMusic(item, true) :  () => this.loveMusic(item, false) } className="songLove" />
								<img onClick={ menuList.indexOf(item.id) == -1 ? () => this.addMusic(item) : () => this.delMusic(item) } className="songAdd" src={menuList.indexOf(item.id) == -1 ? add : error} />
								{
									id === item.id ? ( isPause ? <img className="songPlay" src={play} onClick={() => this.musicPlay(false)} /> : <img className="songPlay" src={stop} onClick={() => this.musicPlay(true)} /> ) : <img className="songPlay" src={play} onClick={() => this.changeMusic(item)} />
								}
							</div>
						})
					}
				</div>

				<div className="recommend2">
					<div className="recommend_title2">推荐歌单</div>
					{
						dang.map((item, index)=>{
							if(index < 9) {
								return <div className="recommend_box2" key={index} onClick={() => this.pushSong2(item)}>
									<img className="recommend_img2" src={item.picUrl} />
									<div className="recommend_text2">{item.name}</div>
								</div>
							}
						})
					}
				</div>

			</div>
		);
	}
}

export default recommend;